/**
 * Show left block (about) by click
 */
$(function(){"use strict";$(".js-drawer-toggle").click(function(a){a.preventDefault();var b=$(this).attr("data-target"),c=$(b);c.length&&(c.hasClass("hide")?(c.removeClass("hide"),c.animate({left:0})):c.animate({left:-1*c.width()},400,"swing",function(){c.addClass("hide")}))})});